import React, { useEffect, useState } from "react";
import { Card, Col, Container, Row, Image, Table } from "react-bootstrap";
import { FormattedMessage } from "react-intl";
import Cama from "../assets/Cama.png";
import Cocina from "../assets/Cocina.png";
import Comedor from "../assets/Comedor.png";
import Sala from "../assets/Sala.jpeg";
import Devices from "./Devices"




function Room(props) {
    const [rooms, setRooms] = useState([]);
    const [selectedRoom, setSelectedRoom] = useState(null);

    const urlRooms =
      "https://gist.githubusercontent.com/josejbocanegra/92c90d5f2171739bd4a76d639f1271ea/raw/9effd124c825f7c2a7087d4a50fa4a91c5d34558/rooms.json";

    useEffect(() => {axios
        .get(urlSpaces)
        .then((jsonData) => {
          console.log("JSON Data rooms", jsonData.data);
          localStorage.setItem("rooms", JSON.stringify(jsonData.data));
          // eslint-disable-next-line array-callback-return
          jsonData.data.map((e) => {
            if (e.homeId === props.selectedSpace.id) {
              currentRooms.push(e);
            }
          });
          setRooms(currentRooms);
        })
        .catch((error) => {
          console.error(error);
          if (localStorage.getItem("rooms") === null) {
            setRooms([]);
          } else {
            let r = JSON.parse(localStorage.getItem("rooms"));
            // eslint-disable-next-line array-callback-return
            r.map((e) => {
              if (e.homeId === props.selectedSpace.id) {
                currentRooms.push(e);
              }
            });

            setRooms(currentRooms);
          }
        });
}, [props, props.selectedSpace]);
    console.log("Rooms ", rooms);



    const isSelectedRoomNull = selectedRoom === null;

    return (
      <div>
        <h2
          className="roomTitle"
          style={{ textAlign: "left", marginLeft: "2rem" }}
        >
          <FormattedMessage id="rooms" />
        </h2>
        <Container>
          <Row>
            <Col md="7">
              <Row>
                {rooms.map((e, i) => (
                  <Col md="4" key={`room ${i}`}>
                    <Card
                      onClickCapture={() => {
                        setSelectedRoom(e);
                        console.log("selectedRoom ", selectedRoom);
                      }}
                    >
                      <Card.Title className="roomTitle">
                        {RommName(e.name)}
                      </Card.Title>
                      <Image src={Imagen(e.name)} />
                    </Card>
                  </Col>
                ))}
              </Row>
            </Col>
            <Col>
              <Table striped bordered>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>ID</th>
                    <th>
                      <FormattedMessage id="device" />
                    </th>
                    <th>
                      <FormattedMessage id="value" />
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {console.log(
                    "devices ",
                    selectedRoom !== null ? selectedRoom.devices : []
                  )}
                  {!isSelectedRoomNull &&
                    selectedRoom.devices.map((e, i) => (
                      <Devices id={i} device={e} />
                    ))}
                </tbody>
              </Table>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
function  Imagen(roomName){

    if (roomName ==="Kitchen")
      return Cocina;    
    else if (roomName ==="Dinner room")
      return Comedor;
    else if (roomName ==="Living room")
      return Sala;
    else
      return Cama;
}

function RommName(roomName)
{
    if (roomName ==="Kitchen")
        return <FormattedMessage id="kitchen" />; 
    else if (roomName ==="Dinner room")
        return <FormattedMessage id="dinnerRoom" />; 
    else if (roomName ==="Living room")
      return Sala;
    else
      return Cama;
}

  export default Room;
